import React, { useState, useContext } from "react";
import { StateContext } from "../StateContext";
import {format, formatISO9075} from "date-fns"

const Post = ({summary, title, image, createdAt, updatedAt, content, author})=>{
   console.log(image)
    return(
        <div className="post">

        <div className="image">
        <img src={"http://localhost:4000/uploads\0e0283b0e85a93110c3a7fafe1ce6220.jpg"} alt=""/>
        </div>

        <section className="content"><h2>{title}</h2>
        <p className="info">  <a className="author">{author.username}</a> <time>{format(new Date(createdAt), 'MMM d, yyy HH:mm')}</time></p> 
        <p className="summary">{summary}</p>
        <p className="summary">{content}</p>
        </section>
        
       
        </div>
       
    )
}

export default Post;
