import React, { useContext } from "react";
import Post from "./Post";
import { StateContext } from "../StateContext";


const Blog = ()=>{
    const {status, setStatus} = useContext(StateContext);
    
    return(
        status &&
        (<section>
          <form>
            <input type="text" name="post" placeholder="Share your post"/>
          </form>
        </section>)
    )
}

export default Blog;
