import React, { useContext } from "react";
import Post from "./Post";
import { StateContext } from "../StateContext";


const Blog = ()=>{
    const {status, setStatus} = useContext(StateContext);
    
    return(
        status &&
        (<section>
          <form className="login" method="POST" onSubmit={onLogin}>
            <input type="text" name="title" placeholder="Your Blog title"/>
            <input type="text" name="post" placeholder="Share your post"/>
            <input type="text" name="catphotourl" placeholder="cat photo URL" required/>
            <button type="submit">Submit</button>
          </form>
        </section>)
    )
}

export default Blog;
