import { useParams } from "react-router-dom";




const PostPage = ()=>{

    const [postInfo, setPostInfo] = useState({})
    const {id} = useParams();
    console.log(id)
  
    useEffect(
    ()=>{
       fetch(`http://localhost:4000/post/${id}`,{
        method: "GET",
        credentials: "include"
       }).then(response=>{
          response.json().then(post=>{
            console.log(post.author)
          })
       })
    }, []);

return(
    <div>

    </div>
)
}

export default PostPage;
