import React, { useState, useContext } from "react";
import Post from "./Post";
import { StateContext } from "../StateContext";


const Blog = ()=>{
    const [post, setPost] = useState({title:"", content:"", url:""})
    const {status} = useContext(StateContext);
    
    const postData = (event)=>{
       const name = event.target.name;
       console.log(name)
       const value = event.target.value;
        setPost(prevValue=>{
            return(
                {
                  ...prevValue,
                  [name] : value  
                }
            )
        })
    }
    
   
    return(
        status &&
        (<section>
          <form className="post" >
            <input onChange={postData} type="text" name="title" placeholder="Your Blog title"  value={post.title}/>
            <input onChange={postData} type="text" name="content" placeholder="Share your post" value={post.content}/>
            <input onChange={postData} type="text" name="url" placeholder="Blog photo URL" value={post.url} required/>
            <button type="submit">Submit</button>
          </form>
        </section>)
    )
}

export default Blog;
