export default function EditPost() {

    const [post, setPost] = useState({title:"", summary:""});
    const [content, setContent] = useState("");
    const [files, setFiles] = useState({});
    const [redirect, setRedirect] = useState(false);
    
    const postData = (event)=>{
        const name = event.target.name;
        console.log(name)
        const value = event.target.value;
         setPost(prevValue=>{
             return(
                 {
                   ...prevValue,
                   [name] : value  
                 }
             )
         })
     }
 
     const postFiles = event=> {
       setFiles(event.target.files[0]);
     }

    if(redirect){
        return (<Navigate to={'/'} />)
        } 

  return(
    <section>
          <form className="post" onSubmit={onSubmit}>
            <input onChange={postData} 
            type="text" name="title" 
            placeholder="Your Blog title"  
            value={post.title} required/>

            <input onChange={postData} 
            type="text" name="summary" 
            placeholder="Summary" value=
            {post.summary}/>

            <input onChange={postFiles} ref={inputRef} 
            type="file" name="image"/>

            <ReactQuill onChange={newValue=>setContent(newValue)} 
            name="content" value={content} modules={modules} formats={formats} />
            <button id="post-btn" type="submit">Edit Post</button>
          </form>
        </section>
  )
}
