import React, { useState } from "react";
import { Outlet } from "react-router-dom";

const Login = ()=>{
  const [loginInput, setLoginInput] = useState({username:"",password:""});

  const loginInputHandler = event=>{
    const  {name, value} = event.target;
    console.log(event.target)
    setLoginInput(prevValue=>{
     return ({
        ...prevValue,
        [name]: value
      })
    })
  }
    return(
       <form className="login" method="POST" action="">
       <h1>Login</h1>
         <input onChange={loginInputHandler} type="text" value={loginInput.username} />
         <input onChange={loginInputHandler} type="password" value={loginInput.password} />
         <button>Login</button>
       </form>
    )
}

export default Login;
