import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";




const PostPage = ()=>{


    const {id} = useParams();
    console.log(id)
  
    useEffect(
    ()=>{
       fetch(`http://localhost:4000/post/${id}`,{
        method: "GET",
        credentials: "include"
       }).then(response=>{
          console.log(response.json().then(post=>{
            console.log(post.author)
          }))
       })
    }, []);

return(
    <div>

    </div>
)
}

export default PostPage;
