import React, { useContext, useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { Navigate } from "react-router-dom";
import { StateContext } from "../StateContext";
import { onLogout } from "../functions/Logout";
import { fetchData } from "../functions/fetchData";

const Header = ()=>{

const {status, setStatus} = useContext(StateContext);
console.log(status.state)
console.log(status.user)

useEffect(()=>{
   fetchData(setStatus);
})

    return(
        <header>
            <Link to="/" className="Logo">My Blog</Link>
            <nav>
            {status.state && (
                <>
                <Link to="/blog" className="nav-icon">Blog</Link>
                <a href="" onClick={()=>{onLogout(setStatus)}} className="nav-icon">Logout</a>  
                </>
            )}
            {!status.state && (<>
               <Link to="/register" className="nav-icon" >Register</Link>
               <Link to="/login" className="nav-icon">Login</Link>
            </>)}
            
             
            </nav>
        </header>
    )
}

export default Header;
