import React, { useContext, useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { StateContext } from "../StateContext";
import { onLogout } from "../functions/Logout";
import { fetchData } from "../functions/fetchData";


const Header = ()=>{

const {status, setStatus} = useContext(StateContext);
const [color, setColor] = useState(false);
const changeColor = ()=>{
    if(window.scrollY>=300){
      setColor(true);
    }
    else{
        setColor(false);
    }
}
//console.log(status)

window.addEventListener('scroll', changeColor);

useEffect(()=>{
   fetchData(setStatus);
}, [])

    return(
        <header className={color && "header-bg"}>
            <Link to="/" className="Logo">404 <span>Owl</span></Link>
            <nav>
            {status.state && (
                <>
                <Link to="/blog" className="nav-icon create-blog">New Blog</Link>
                <a href="" onClick={()=>{onLogout(setStatus)}} className="nav-icon">Logout</a>  
                </>
            )}
            {!status.state && (<>
               <Link to="/login" className="nav-icon">Log in</Link>
               <Link to="/register" className="nav-icon sign-up" >Sign up</Link>
            </>)}
            
             
            </nav>
        </header>
    )
}

export default Header;
