import React, { useState } from "react";
import { Outlet } from "react-router-dom";

const Register = ()=>{

  const [Input, setInput]=useState({username:"",password:""});

  const setInputHandler = event=>{
      const userInput = event.target.value;
      const inputName = event.target.name;
     
      setInput(prevValue=>{
        return({
           ...prevValue,
           [inputName]:userInput
        })
      });
  }

  const onRegister = event=>{
    event.preventDefault();
    Input="";
  }

    return(
       <form className="register" method="post" action="" onSubmit={onRegister}>
         <h1>Register</h1>
         <input onChange={setInputHandler} type="text" name="username" value={Input.username} />
         <input onChange={setInputHandler} type="password" name="password" value={Input.password}/>
         <button>Register</button>
       </form>
    )
}

export default Register;
