import React, { useContext, useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { useParams } from "react-router-dom";
import {format, formatISO9075} from "date-fns"
import { StateContext } from "../StateContext";




const PostPage = ()=>{
 
   const [postInfo, setPostInfo] = useState({
        content:"", image:"", summary:"", title:"", _id:"", 
        createdAt:new Date(),author:{username:"", _id:""}
       })

    const {id} = useParams();
    const {status, setStatus} = useContext(StateContext);

    // console.log(postInfo);
    // console.log(postInfo.image);
    // console.log(status);
    // console.log(postInfo.author._id)
    //console.log(status.user)


    useEffect(
        ()=>{
           fetch(`http://localhost:4000/post/${id}`,{
            method:'GET',
            credentials:'include'
           }).then(response=>{
              response.json().then(user=>{
                //typeOf(post.image)
                setPostInfo(user.post);
                console.log(user.post)
              })
           })
    }, []);
   
 

const deletePost = ()=>{

}

 
if(!postInfo){
    return '';
}
else{
    return( 
        <div className="single-post">
        <div className="head">
        <p className="single-info">  <a className="single-author">{postInfo.author.username}</a> <time>{format(new Date(postInfo.createdAt), 'MMM d, yyy HH:mm')}</time></p> 
        {status.user && postInfo.author._id === status.user._id &&
        (<div>
        <Link id="single-link" to={`/edit/${postInfo._id}`}><svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth={1.5} stroke="currentColor" className="w-6 h-6">
  <path strokeLinecap="round" strokeLinejoin="round" d="M16.862 4.487l1.687-1.688a1.875 1.875 0 112.652 2.652L10.582 16.07a4.5 4.5 0 01-1.897 1.13L6 18l.8-2.685a4.5 4.5 0 011.13-1.897l8.932-8.931zm0 0L19.5 7.125M18 14v4.75A2.25 2.25 0 0115.75 21H5.25A2.25 2.25 0 013 18.75V8.25A2.25 2.25 0 015.25 6H10" />
</svg> Edit</Link>
        <a href="" onClick={deletePost}>Delete</a>
        </div>)}
        </div>
        <section className="content">
        <h2>{postInfo.title}</h2>
        <p className="single-summary">{postInfo.summary}</p>
        <div className="single-image">
        <img src={"http://localhost:4000/" + postInfo.image}/>
        </div>
        <p className="single-content" dangerouslySetInnerHTML={{__html:postInfo.content}}/>
        </section>
        
       
        </div>
    )
}

}

export default PostPage;
