import React, { useState, useContext } from "react";
import Post from "./Post";
import { StateContext } from "../StateContext";


const Blog = ()=>{
    const [post, setPost] = useState({})
    const {status, setStatus} = useContext(StateContext);
    
    const postData = (event)=>{
       const name = event.target.name;
       console.log(name)
       const value = event.target.value;
        setPost(prevValue=>{
            return(
                {
                  ...prevValue,
                  [name] : value  
                }
            )
        })
    }
    
    const onSubmit= event=>{
        event.preventDefault();
        setStatus(null);
    }

    return(
        status &&
        (<section>
          <form className="post" onSubmit={onSubmit}>
            <input onChange={postData} type="text" name="title" placeholder="Your Blog title"  value={post.name}/>
            <input onChange={postData} type="text" name="content" placeholder="Share your post" value={post.name}/>
            <input onChange={postData} type="text" name="url" placeholder="Blog photo URL" value={post.name} required/>
            <button type="submit">Submit</button>
          </form>
        </section>)
    )
}

export default Blog;
