import React, { useContext, useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { useParams } from "react-router-dom";
import {format, formatISO9075} from "date-fns"
import { StateContext } from "../StateContext";




const PostPage = ()=>{
 
   const [postInfo, setPostInfo] = useState({
        content:"", image:"", summary:"", title:"", _id:"", 
        createdAt:new Date(),author:{username:"", _id:""}
       })

    const {id} = useParams();
    const {status, setStatus} = useContext(StateContext);

    // console.log(postInfo);
    // console.log(postInfo.image);
    // console.log(status);
    // console.log(postInfo.author._id)
    console.log(status.user)


    useEffect(
        ()=>{
           fetch(`http://localhost:4000/post/${id}`,{
            method:'GET',
            credentials:'include'
           }).then(response=>{
              response.json().then(user=>{
                //typeOf(post.image)
                setPostInfo(user.post);
                console.log(user.post)
              })
           })
    }, []);
   
 

// const editPost = ()=>{
//     fetch(`http://localhost:4000/edit/${id}`,{
//         method:'PATCH',
//         credentials:'include'
//     }).then(response=>{
//         response.json().then(post=>{
//                 setPostInfo({content: post});
//         })
//     })
// }

const deletePost = ()=>{

}

 
if(!postInfo){
    return '';
}
else{
    return( 
        <div className="post">

        <div className="image">
        <img src={"http://localhost:4000/" + postInfo.image}/>
        </div>
        {status.user && postInfo.author._id === status.user._id &&
        (<div>
        <Link to={`/edit/${postInfo._id}`}>Edit</Link>
        <a href="" onClick={deletePost}>Delete</a>
        </div>)}
        <p className="info">  <a className="author">{postInfo.author.username}</a> <time>{format(new Date(postInfo.createdAt), 'MMM d, yyy HH:mm')}</time></p> 
        <section className="content">
        <h2>{postInfo.title}</h2>
        <p className="summary">{postInfo.summary}</p>
        <p className="summary" dangerouslySetInnerHTML={{__html:postInfo.content}}/>
        </section>
        
       
        </div>
    )
}

}

export default PostPage;
