import React, { useContext, useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import {format, formatISO9075} from "date-fns"
import { StateContext } from "../StateContext";



const PostPage = ()=>{
  
    const [postInfo, setPostInfo] = useState({createdAt:new Date(),author:{username:"", _id:""}})
    const {id} = useParams();
    const {status, setStatus} = useContext(StateContext);
    
    useEffect(
    ()=>{
       fetch(`http://localhost:4000/post/${id}`,{
        method:'GET',
        credentials:'include'
       }).then(response=>{
          response.json().then(post=>{
            //typeOf(post.image)
            setPostInfo(post);
            
          })
       })
}, []);

console.log(postInfo.author._id)
    console.log(status.user._id)

 
if(!postInfo){
    return '';
}
else{
    return( 
        <div className="post">

        <div className="image">
        <img src={"http://localhost:4000/" + postInfo.image}/>
        </div>
        {postInfo.author.id === status.user.id?(<button>Edit</button>):null}
        <p className="info">  <a className="author">{postInfo.author.username}</a> <time>{format(new Date(postInfo.createdAt), 'MMM d, yyy HH:mm')}</time></p> 
        <section className="content">
        <h2>{postInfo.title}</h2>
        <p className="summary">{postInfo.summary}</p>
        <p className="summary" dangerouslySetInnerHTML={{__html:postInfo.content}}/>
        </section>
        
       
        </div>
    )
}

}

export default PostPage;
